import React, { useEffect, useState } from 'react';
import { getReservations } from './api';

function App() {
  const [reservations, setReservations] = useState<Reservation[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Definimos la interfaz nuevamente para el componente
  interface Reservation {
    id: number;
    user_id: number;
    reservation_date: string;
    reservation_time: string;
    guests: number;
    special_requests: string | null;
    status: 'pending' | 'confirmed' | 'cancelled';
  }

  useEffect(() => {
    const fetchReservations = async () => {
      try {
        const data = await getReservations();
        setReservations(data);
      } catch (err) {
        setError('Error al cargar las reservaciones');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchReservations();
  }, []);

  if (loading) return <div className="loading">Cargando reservaciones...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div style={{ maxWidth: '800px', margin: '0 auto', padding: '20px' }}>
      <h1 style={{ textAlign: 'center' }}>Gestión de Reservas</h1>

      <div style={{ marginTop: '20px' }}>
        {reservations.length === 0 ? (
          <p>No hay reservaciones registradas</p>
        ) : (
          reservations.map(reservation => (
            <div 
              key={reservation.id} 
              style={{
                border: '1px solid #ddd',
                borderRadius: '8px',
                padding: '15px',
                marginBottom: '15px',
                backgroundColor: '#f9f9f9'
              }}
            >
              <h3 style={{ marginTop: '0', color: '#333' }}>
                Reservación #{reservation.id}
              </h3>
              <p><strong>Fecha:</strong> {reservation.reservation_date}</p>
              <p><strong>Hora:</strong> {reservation.reservation_time}</p>
              <p><strong>Invitados:</strong> {reservation.guests}</p>
              {reservation.special_requests && (
                <p><strong>Peticiones especiales:</strong> {reservation.special_requests}</p>
              )}
              <p>
                <strong>Estado:</strong> 
                <span 
                  style={{
                    color: reservation.status === 'pending' ? '#ff9800' : 
                           reservation.status === 'confirmed' ? '#4caf50' : '#f44336',
                    fontWeight: 'bold',
                    marginLeft: '5px'
                  }}
                >
                  {reservation.status === 'pending' ? 'Pendiente' : 
                   reservation.status === 'confirmed' ? 'Confirmada' : 'Cancelada'}
                </span>
              </p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default App;
