import React, { useEffect, useState } from 'react';
import { getBookings, Reservation } from './api'; // Importa la interfaz desde api.ts

function App() {
  const [reservations, setReservations] = useState<Reservation[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await getBookings();
        setReservations(data);
      } catch (err) {
        setError('Error al cargar las reservaciones');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <div>Cargando...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div style={{ padding: '20px' }}>
      <h1>Mis Reservas</h1>
      {reservations.length === 0 ? (
        <p>No hay reservaciones registradas</p>
      ) : (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {reservations.map(reservation => (
            <li key={reservation.id} style={{
              margin: '10px 0',
              padding: '10px',
              border: '1px solid #ddd',
              borderRadius: '4px'
            }}>
              <strong>{reservation.service_type}</strong>
              <div>Fecha: {new Date(reservation.date).toLocaleDateString()}</div>
              <div>Hora: {reservation.time}</div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
